import React, { useState } from 'react';

const Login = () => {
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    const value = e.target.value;
    setPassword(value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your password verification logic here
    if (password !== 'correct_password') { // Replace 'correct_password' with your actual password logic
      setErrorMessage('Incorrect password');
    } else {
      setErrorMessage('');
      // Proceed with successful login actions
    }
  };

  return (
    <div className="flex flex-col justify-center items-center min-h-screen w-full bg-dark-gray text-white">
      <h1 className="text-2xl mb-8">To gain admin access, please enter password</h1>
      <form onSubmit={handleSubmit} className="flex flex-col items-center w-full max-w-sm">
        <div className="relative w-full">
          <input
            type="text"
            value={password.replace(/./g, '•')}
            onChange={handleChange}
            className="mb-4 p-2 rounded w-full border border-white"
            placeholder="Enter Password"
            style={{ color: 'black' }}
          />
          <input
            type="password"
            value={password}
            onChange={handleChange}
            className="absolute top-0 left-0 opacity-0 w-full h-full"
            tabIndex="-1"
          />
        </div>
        <button
          type="submit"
          className="bg-white text-navy py-2 px-4 rounded-lg"
        >
          Submit
        </button>
      </form>
      {errorMessage && (
        <div className="mt-4 text-red-500">
          {errorMessage}
        </div>
      )}
    </div>
  );
};

export default Login;
