import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useLocation } from 'react-router-dom';
import './App.css';
import 'tailwindcss/tailwind.css';
import './output.css';
import realEstateLogo from './images/realEstateLogo.png'; // Importing the logo image
import { getUsers } from './firebase'; // Importing the firebase module
import Home from './components/Home';
import Announcements from './components/Announcements';
import Calendar from './components/Calendar';
import About from './components/About';
import Merchandise from './components/Merchandise';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    // Handle login functionality here
    setLoggedIn(true);
  };

  const handleLogout = () => {
    // Handle logout functionality here
    setLoggedIn(false);
  };

  return (
    <Router>
      <div>
        {/* Banner with Logo and Login button */}
        <div className="header-container bg-blue-400 text-white py-4 relative flex justify-between items-center">
          <div className="flex items-center">
            <img src={realEstateLogo} alt="Real Estate Logo" className="h-20 mr-2" />
            <div>
              {/* Welcome Message */}
            </div>
          </div>
          <div className="mr-4">
            {/* Login button */}
            {loggedIn ? (
              <button
                className="bg-white text-blue-500 py-2 px-4 rounded-lg btn"
                onClick={handleLogout}
              >
                Logout
              </button>
            ) : (
              <button
                className="bg-white text-blue-500 py-2 px-4 rounded-lg btn"
                onClick={handleLogin}
              >
                Login
              </button>
            )}
          </div>
        </div>

        {/* Tabs */}
        <Tabs />

        {/* Main content */}
        <div className="flex justify-center">
          <Routes>
          <Route path="/" element={<Home/>}/>
            <Route path="/announcements" element={<Announcements />} />
            <Route path="/calendar" element={<Calendar />} />
            <Route path="/about" element={<About />} />
            <Route path="/merch" element={<Merchandise />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

function Tabs() {
  const location = useLocation();

  const getTabClass = (path) => {
    return location.pathname === path ? 'bg-blue-200 text-gray-8--' : 'bg-gray-200 text-gray-800';
  };

  return (
    <div className="flex justify-between w-full">
      <Link to="/" className={`w-full px-4 py-4 text-center ${getTabClass('/')}`}>Home</Link>
      <Link to="/announcements" className={`w-full px-4 py-4 text-center ${getTabClass('/announcements')}`}>Announcements</Link>
      <Link to="/calendar" className={`w-full px-4 py-4 text-center ${getTabClass('/calendar')}`}>Calendar</Link>
      <Link to="/about" className={`w-full px-4 py-4 text-center ${getTabClass('/about')}`}>About</Link>
      <Link to="/merch" className={`w-full px-4 py-4 text-center ${getTabClass('/merch')}`}>Merch</Link>
    </div>
  );
}

export default App;
