import React from 'react';
import eboard1 from '../images/Eboard1.jpeg';
import eboard2 from '../images/Eboard2.jpg';
import blake from '../images/Blake.jpeg';
import jacob from '../images/Jacob.jpg';
import sophie from '../images/Sophie.jpg';
import caleb from '../images/Caleb.jpg';
import bronson from '../images/Bronson.jpg';
import jake from '../images/Jake.jpg';

const members = [
  { name: 'Blake', image: blake, description: 'Description for Blake', title: 'President', email: 'bessb@mail.gvsu.edu' },
  { name: 'Jacob', image: jacob, description: 'Description for Jacob', title: 'Vice President', email: 'vandenjd@mail.gvsu.edu' },
  { name: 'Sophie', image: sophie, description: 'Description for Sophie', title: 'Treasurer', email: 'buloss@mail.gvsu.edu' },
  { name: 'Caleb', image: caleb, description: 'Description for Caleb', title: 'Secretary', email: 'raycal@mail.gvsu.edu' },
  { name: 'Bronson', image: bronson, description: 'Description for Bronson', title: 'Marketing Director', email: 'bessbr@mail.gvsu.edu' },
  { name: 'Jake', image: jake, description: 'Description for Jake', title: 'Event Coordinator', email: 'giddingsj@amail.gvsu.edu' },
  { name: 'Jake', image: jake, description: 'Cam Raffler', title: 'Event Coordinator', email: 'giddingsj@amail.gvsu.edu' },
];

const About = () => {
  return (
    <div style={{ padding: '60px', fontFamily: 'Arial, sans-serif', margin: '0 auto', width: '75%' }}>
      <h1 className="text-4xl font-bold" style={{ textAlign: 'center' }}>About Us!</h1>
      <section style={{ marginBottom: '20px', textAlign: 'left' }}></section>
      {members.map((member, index) => (
        <div
          key={index}
          style={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'flex-start', 
            margin: '20px 0',
            padding: '20px 0', 
            borderBottom: '1px solid #ccc' 
          }}
        >
          <img 
            src={member.image} 
            alt={member.name} 
            style={{ 
              width: '150px', 
              height: '200px', 
              borderRadius: '10px', 
              marginRight: '20px' 
            }} 
          />
          <div style={{ textAlign: 'left', flex: 1 }}>
            <h2 className="text-3xl font-bold">{member.name}</h2>
            <h3 className="text-xl font-bold">{member.title}</h3>
            <a href={`mailto:${member.email}`} style={{ color: 'gray', fontStyle: 'italic' }}>{member.email}</a>
            <p style={{ paddingTop: '20px' }}>{member.description}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default About;
