import React, { useState, useEffect } from 'react';

const Calendar = () => {
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    const iframe = document.querySelector('iframe');
    const handleLoad = () => {
      setIsLoaded(true);
    };

    if (iframe) {
      iframe.addEventListener('load', handleLoad);
    }

    return () => {
      if (iframe) {
        iframe.removeEventListener('load', handleLoad);
      }
    };
  }, []);

  return (
    <div className="flex justify-center items-center" style={{ width: '100vw', height: '100vh' }}>
      {!isLoaded && <div>Loading...</div>}
      {isLoaded && (
        <iframe
          src="https://calendar.google.com/calendar/embed?src=emiller3425%40gmail.com&ctz=America%2FDetroit"
          style={{
            width: '100vw',
            height: '100vh',
            filter: 'invert(0.9) saturate(0.5) hue-rotate(145deg)',
          }}
          frameBorder="0"
        ></iframe>
      )}
    </div>
  );
};

export default Calendar;
