import React, { useState } from 'react';
import eboard1 from '../images/Eboard1.jpeg';

const Home = () => {
  const [welcomeText, setWelcomeText] = useState(`
    Welcome to the GVSU Real Estate Club

    Who We Are

    Welcome to the Real Estate Club, where we bring together enthusiasts, professionals,
    and students passionate about the dynamic world of real estate. Our mission is to provide
    valuable insights, networking opportunities, and educational resources to help our members
    navigate the real estate industry effectively.

    Whether you are interested in residential, commercial, or industrial real estate, our club
    offers a platform for growth and development. Join us for informative seminars, engaging
    discussions, and hands-on workshops designed to enhance your understanding and skills
    in the real estate market.
  `);

  const handleTextChange = (event) => {
    setWelcomeText(event.target.value);
  };

  const handleSave = () => {
    // You can implement the logic to save the text to a backend or local storage here
    console.log('Text saved:', welcomeText);
  };

  return (
    <div style={{ 
      padding: '20px', 
      fontFamily: 'Arial, sans-serif', 
      textAlign: 'center', 
      margin: '0 auto' 
    }}>
      <div 
        style={{ 
          backgroundImage: `url(${eboard1})`, 
          backgroundSize: 'cover', 
          backgroundPosition: 'top', 
          width: '100vw',
          height: '100vh',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          color: 'white',
          position: 'relative',
        }}
      >
        <textarea
          value={welcomeText}
          onChange={handleTextChange}
          style={{
            width: '100%',
            height: '100%',
            background: 'none',
            border: 'none',
            fontSize: '18px',
            color: 'white',
            padding: '20px',
            resize: 'none',
          }}
        />
        <button
          onClick={handleSave}
          style={{
            position: 'absolute',
            bottom: '20px',
            right: '20px',
            padding: '10px 20px',
            backgroundColor: '#004B80',
            color: '#fff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '16px',
            transition: 'background-color 0.3s',
          }}
        >
          Save
        </button>
      </div>
    </div>
  );
};

export default Home;
