import React, { useState, useEffect } from 'react';
import seidman from '../images/seidman.jpg';
import eboard1 from '../images/Eboard1.jpeg';
import eboard2 from '../images/Eboard2.jpg';

const images = [seidman, eboard1, eboard2];

const Home = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ 
      fontFamily: 'Arial, sans-serif', 
      textAlign: 'center', 
      margin: '0 auto' 
    }}>
      <div 
        style={{ 
          position: 'relative',
          width: '100vw',
          height: '100vh',
          overflow: 'hidden',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          color: 'white',
        }}
      >
        {images.map((image, index) => (
          <img 
            key={index}
            src={image} 
            alt={`Slide ${index + 1}`} 
            style={{
              position: 'absolute',
              top: '0',
              left: '0',
              width: '100%',
              height: '100%',
              objectFit: 'cover',
              zIndex: '-1',
              opacity: currentImageIndex === index ? 1 : 0,
              transition: 'opacity 1s ease-in-out'
            }}
          />
        ))}
        <div
          style={{
            position: 'absolute',
            top: '0',
            left: '0',
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0, 43, 128, 0.15)', // navy color with 15% opacity
            zIndex: '0'
          }}
        />
        <h1 className="text-4xl font-bold" style={{ textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)', zIndex: '1' }}>
          Welcome to the GVSU Real Estate Club
        </h1>
      </div>

      <div style={{ margin: '40px 0', padding: '20px' }}>
        <h2 className="text-4xl font-bold" style={{ padding: '20px' }}>Next Meeting</h2>
        <p>Join us for our next meeting on <strong>June 15th, 2024</strong> at <strong>6:00 PM</strong> in the Seidman College of Business, Room 101.</p>
        <p>We will be discussing the current trends in the real estate market and networking opportunities. Don't miss it!</p>
      </div>

      <section style={{ marginBottom: '20px', textAlign: 'left', width: '60%', margin: '0 auto' }}>
        <h2 className='text-4xl font-bold text-center' style={{ padding: '20px' }}>Our Mission</h2>
        <p>
          Welcome to the Real Estate Club, where we bring together enthusiasts, professionals, 
          and students passionate about the dynamic world of real estate. Our mission is to provide 
          valuable insights, networking opportunities, and educational resources to help our members 
          navigate the real estate industry effectively.
        </p>
        <p>
          Whether you are interested in residential, commercial, or industrial real estate, our club 
          offers a platform for growth and development. Join us for informative seminars, engaging 
          discussions, and hands-on workshops designed to enhance your understanding and skills 
          in the real estate market.
        </p>
      </section>

      <a 
        href="mailto:reclub@mail.gvsu.edu"
        style={{
          display: 'inline-block',
          padding: '10px 20px',
          backgroundColor: '#004B80',
          color: '#fff',
          textDecoration: 'none',
          border: 'none',
          borderRadius: '5px', // Removed rounded corners
          cursor: 'pointer',
          fontSize: '16px',
          transition: 'background-color 0.3s',
          margin: '40px auto', // Add margin to create space around the button
          whiteSpace: 'nowrap' // Prevent text from wrapping
        }}
        className="hover:transform hover:scale-110 text-white font-bold py-2 px-4 mb-4"
      >
        Contact Us
      </a>
    </div>
  );
};

export default Home;
