import React, { useState, useEffect } from 'react';
import seidman from '../images/seidman.jpg';
import eboard1 from '../images/Eboard1.jpeg';
import eboard2 from '../images/Eboard2.jpg';

// Array of images for the slideshow
const images = [seidman, eboard1, eboard2];

const Home = ({ adminAccess }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0); // State to track the current image index in the slideshow
  const [editMode, setEditMode] = useState(false); // State to track whether edit mode is enabled
  const [content, setContent] = useState({
    welcomeMessage: '',
    nextMeetingTitle: '',
    nextMeeting: '',
    missionTitle: '',
    mission: '',
  });

  useEffect(() => {
    // Fetch content from the server when the component mounts
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:5001/home-content');
        const data = await response.json();
        setContent({
          welcomeMessage: data.welcomeMessage,
          nextMeetingTitle: data.nextMeeting.title,
          nextMeeting: data.nextMeeting.content,
          missionTitle: data.mission.title,
          mission: data.mission.content,
        });
      } catch (error) {
        console.error('Error fetching content:', error);
      }
    };

    fetchData();

    // Set an interval to change the image in the slideshow every 4 seconds
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 4000);

    // Clear the interval when the component unmounts
    return () => clearInterval(interval);
  }, []);

  // Handle changes to the input fields in edit mode
  const handleChange = (e) => {
    const { name, value } = e.target;
    setContent((prevContent) => ({ ...prevContent, [name]: value }));
  };

  // Handle saving the updated content to the server
  const handleSave = async () => {
    try {
      const response = await fetch('http://localhost:5001/update-home-content', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          welcomeMessage: content.welcomeMessage,
          nextMeeting: { title: content.nextMeetingTitle, content: content.nextMeeting },
          mission: { title: content.missionTitle, content: content.mission },
        }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      setEditMode(false); // Exit edit mode after saving
    } catch (error) {
      console.error('Error updating content:', error);
    }
  };

  return (
    <div className="font-sans text-center mx-auto">
      <div 
        className="relative w-full h-screen overflow-hidden flex items-center justify-center text-white"
      >
        {/* Slideshow images */}
        {images.map((image, index) => (
          <img 
            key={index}
            src={image} 
            alt={`Slide ${index + 1}`} 
            className={`absolute top-0 left-0 w-full h-full object-cover z-[-1] transition-opacity duration-1000 ${currentImageIndex === index ? 'opacity-100' : 'opacity-0'}`}
          />
        ))}
        <div className="absolute top-0 left-0 w-full h-full bg-[rgba(0,43,128,0.15)] z-0"></div>
        {/* Welcome message */}
        {editMode ? (
          <input
            type="text"
            name="welcomeMessage"
            value={content.welcomeMessage}
            onChange={handleChange}
            className="text-4xl font-bold text-center bg-[rgba(0,0,0,0.5)] text-white border-none outline-none shadow-lg z-1"
          />
        ) : (
          <h1 className="text-4xl font-bold shadow-lg z-1">
            {content.welcomeMessage}
          </h1>
        )}
      </div>

      <div className="bg-dark-gray text-white py-20">
        {/* Next Meeting section */}
        {editMode ? (
          <input
            type="text"
            name="nextMeetingTitle"
            value={content.nextMeetingTitle}
            onChange={handleChange}
            className="text-4xl font-bold w-full p-2 mb-4 border border-gray-300 rounded-md"
          />
        ) : (
          <h2 className="text-4xl font-bold py-20">{content.nextMeetingTitle}</h2>
        )}
        {editMode ? (
          <textarea
            name="nextMeeting"
            value={content.nextMeeting}
            onChange={handleChange}
            className="w-full p-2 border border-gray-300 rounded-md w-3/5 mx-auto text-left"
            rows="4"
          />
        ) : (
          <p className="text-lg w-3/5 mx-auto text-left">{content.nextMeeting}</p>
        )}
      </div>

      <section className="py-20">
        {/* Mission section */}
        {editMode ? (
          <input
            type="text"
            name="missionTitle"
            value={content.missionTitle}
            onChange={handleChange}
            className="text-4xl font-bold w-full p-2 mb-4 border border-gray-300 rounded-md"
          />
        ) : (
          <h2 className="text-4xl font-bold text-center py-20">{content.missionTitle}</h2>
        )}
        {editMode ? (
          <textarea
            name="mission"
            value={content.mission}
            onChange={handleChange}
            className="w-full p-2 border border-gray-300 rounded-md w-3/5 mx-auto text-left"
            rows="4"
          />
        ) : (
          <p className="text-lg w-3/5 mx-auto text-left">{content.mission}</p>
        )}
      </section>

      {/* Edit/Save button for admin access */}
      {adminAccess && (
        <button
          onClick={editMode ? handleSave : () => setEditMode(true)}
          className="bg-navy text-white py-2 px-4 rounded-lg mb-4"
        >
          {editMode ? 'Save' : 'Edit'}
        </button>
      )}

      {/* Contact Us button */}
      <a 
        href="mailto:reclub@mail.gvsu.edu"
        className="inline-block py-2 px-4 bg-[#004B80] text-white text-decoration-none border-none rounded-lg cursor-pointer text-lg transition-colors duration-300 mx-auto whitespace-nowrap hover:transform hover:scale-110"
      >
        Contact Us
      </a>
    </div>
  );
};

export default Home;
